mapper.select:
select tvmmSeq , tvmmName , tvmmPassword , tvmmDob , tvmmZipCode , tvmmAddress1 , tvmmAddress2 , tvmmAddressFull , tvmmEmailAccount , tvmmEmailDomain , tvmmEmailDomainCd , tvmmTelecomCd , tvmmTelNumber , tvmmAdminNy , tvmmHostNy , tvmmDesc , (select count(*) from tvLodging b where a.tvmmSeq = b.tvmmSeq) as 'lodgingNumber' from tvMember a where 1 = 1 and tvmmDelNy = 0 and tvmmHostNy = 1
and tvmmName like concat('%', #{shValue}, '%') and tvmmEmailAccount like concat('%', #{shValue}, '%')
limit #{startRnumForMysql}, #{rowNumToShow}

select tvmmSeq , tvmmName , tvmmPassword , tvmmDob , tvmmZipCode , tvmmAddress1 , tvmmAddress2 , tvmmAddressFull , tvmmEmailAccount , tvmmEmailDomain , tvmmEmailDomainCd , tvmmTelecomCd , tvmmTelNumber , tvmmAdminNy , tvmmHostNy , tvmmDesc from tvMember where 1 = 1 and tvmmDelNy = 0 and tvmmHostNy = 0 and tvmmAdminNy = 0
and tvmmName like concat('%', #{shValue}, '%') and tvmmEmailAccount like concat('%', #{shValue}, '%')
limit #{startRnumForMysql}, #{rowNumToShow}

mapper.update:
update tvMember set
	tvmmName = #{tvmmName}
	, tvmmDob = #{tvmmDob}
	, tvmmZipCode = #{tvmmZipCode}
	, tvmmAddress1 = #{tvmmAddress1}
	, tvmmAddress2 = #{tvmmAddress2}
	, tvmmAddressFull = #{tvmmAddressFull}
	, tvmmEmailAccount = #{tvmmEmailAccount}
	, tvmmEmailDomain = #{tvmmEmailDomain}
	, tvmmEmailDomainCd = #{tvmmEmailDomainCd}
	, tvmmTelecomCd = #{tvmmTelecomCd}
	, tvmmTelNumber = #{tvmmTelNumber}
	, tvmmDesc = #{tvmmDesc}
where 1=1
	and tvmmSeq = #{tvmmSeq}

update tvLodging set
	tvamLodgingName = #{tvamLodgingName}
	, tvamDesc = #{tvamDesc}
	, tvamZipCode = #{tvamZipCode}
	, tvamAddress1 = #{tvamAddress1}
	, tvamAddress2 = #{tvamAddress2}
	, tvamAddressFull = concat(#{tvamAddress1} ," ", #{tvamAddress2} )
	, tvamState = #{tvamState}
	, tvamCity = #{tvamCity}
	, tvamAdultPrice = #{tvamAdultPrice}
	, tvamMaxPersonCount = #{tvamMaxPersonCount}
	, tvamBedCount = #{tvamBedCount}
	, tvamRoomCount = #{tvamRoomCount}
	, tvamShowerRoomCount = #{tvamShowerRoomCount}
	, tvamApproveNy = #{tvamApproveNy}
	, tvamCategoryCd = #{tvamCategoryCd}
	, tvamSeaNy = #{tvamSeaNy}
	, tvamPoolNy = #{tvamPoolNy}
	, tvamJacuzziNy = #{tvamJacuzziNy}
	, tvamBbqGrillNy = #{tvamBbqGrillNy}
