	, tvamWifiNy = #{tvamWifiNy}
	, tvamTvNy = #{tvamTvNy}
	, tvamParkingRoomNy = #{tvamParkingRoomNy}
	, tvamAirConditionerNy = #{tvamAirConditionerNy}
