mapper.select:
select a.tvcpSeq , a.tvcpName , a.tvcpNumber , a.tvcpPrice , a.tvcpStartDate , a.tvcpEndDate , a.tvcpDesc , a.tvamSeq , b.tvamLodgingName , c.tvmmEmailAccount from tvCoupon a left join tvLodging b on a.tvamSeq = b.tvamSeq left join tvMember c on b.tvmmSeq = c.tvmmSeq where 1 = 1 and tvcpDelNy = 0
and b.tvamLodgingName like concat('%', #{shValue}, '%') and a.tvcpName like concat('%', #{shValue}, '%') and host like concat('%', #{shValue}, '%')
limit #{startRnumForMysql}, #{rowNumToShow}

mapper.update:
update tvCoupon set
	tvcpName = #{tvcpName}
	, tvcpPrice = #{tvcpPrice}
	, tvcpStartDate = #{tvcpStartDate}
	, tvcpEndDate = #{tvcpEndDate}
	, tvcpDesc = #{tvcpDesc}
where 1=1
	and tvcpSeq = #{tvcpSeq}

mapper.select:
select a.tvpmSeq , a.tvpmAdNumber , a.tvpmStartDate , a.tvpmEndDate , a.tvpmPrice , a.tvcpSeq , b.tvcpPrice , a.tvpmtotalPrice , a.tvpmPayTypeCd , a.tvpmDesc , a.tvamSeq , c.tvamLodgingName , c.tvamTelNumber , (select tvmmEmailAccount from tvMember e where e.tvmmSeq = c.tvmmSeq) as 'hostEmail' , a.tvmmSeq , d.tvmmName , d.tvmmTelNumber , d.tvmmEmailAccount from tvPayment a left join tvCoupon b on a.tvcpSeq = b.tvcpSeq left join tvLodging c on a.tvamSeq = c.tvamSeq left join tvMember d on a.tvmmSeq = d.tvmmSeq where 1 = 1 and tvpmDelNy = 0
and c.tvamLodgingName like concat('%', #{shValue}, '%') and d.tvmmEmailAccount like concat('%', #{shValue}, '%') and d.tvmmName like concat('%', #{shValue}, '%')
limit #{startRnumForMysql}, #{rowNumToShow}

mapper.update:
