	, originalName = #{originalName}
	, uuidName = #{uuidName}
	, ext = #{ext}
	, size = #{size}
	, delNy = 0
	, year = #{year}
	, month = #{month}
	, day = #{day}
	, tvmmSeq = #{tvmmSeq}
where 1=1
	and tvmmSeq = #{tvmmSeq}


