mapper.update:
update tvMember set
	tvmmName = #{tvmmName}
	, tvmmDob = #{tvmmDob}
	, tvmmZipCode = #{tvmmZipCode}
	, tvmmAddress1 = #{tvmmAddress1}
	, tvmmAddress2 = #{tvmmAddress2}
	, tvmmAddressFull = #{tvmmAddressFull}
	, tvmmEmailAccount = #{tvmmEmailAccount}
	, tvmmEmailDomain = #{tvmmEmailDomain}
	, tvmmEmailDomainCd = #{tvmmEmailDomainCd}
	, tvmmTelecomCd = #{tvmmTelecomCd}
	, tvmmTelNumber = #{tvmmTelNumber}
	, tvmmDesc = #{tvmmDesc}
where 1=1
	and tvmmSeq = #{tvmmSeq}

mapper.select:
select a.tvamSeq , a.tvamLodgingName , a.tvamHostName , a.tvamDesc , a.tvamZipCode , a.tvamAddress1 , a.tvamAddress2 , a.tvamAddressFull , a.tvamState , a.tvamCity , a.tvamAdultPrice , a.tvamMaxPersonCount , a.tvamBedCount , a.tvamRoomCount , a.tvamShowerRoomCount , a.tvamApproveNy , a.tvamCategoryCd , a.tvamPoolNy , a.tvamJacuzziNy , a.tvamBbqGrillNy , a.tvamFirePlaceNy , a.tvamGymNy , a.tvamWifiNy , a.tvamTvNy , a.tvamParkingRoomNy , a.tvamAirConditionerNy , a.tvamHeaterNy , a.tvamFridgeNy , a.tvamMicrowaveNy , a.tvamCheckInTime , a.tvamCheckOutTime , a.tvmmSeq , a.tvamTelNumber , (select tvcdName from tvCode where a.tvamCategoryCd = tvcdSeq) as 'category' , (select tvmmEmailAccount from tvMember b where a.tvmmSeq = b.tvmmSeq) as 'host' from tvLodging a where 1 = 1 and tvamDelNy = 0
and category like concat('%', #{shValue}, '%') and a.tvamLodgingName like concat('%', #{shValue}, '%') and host like concat('%', #{shValue}, '%')
limit #{startRnumForMysql}, #{rowNumToShow}

