mapper.select:
select a.tvcpSeq ,a.tvcpName ,b.tvamLodgingName ,a.tvcpPrice ,a.tvcpStartDate ,a.tvcpEndDate ,a.tvcpDesc ,a.tvamSeq
from tvCoupon a
left join tvLodging b on a.tvamseq=b.tvamseq
where 1=1 and tvcpDelNy=0 and b.tvmmSeq= #{tvmmSeq}
and a.tvcpName like concat('%', #{shValue}, '%') and b.tvamLodgingName like concat('%', #{shValue}, '%')
limit #{startRnumForMysql}, #{rowNumToShow}

select a.tvcpSeq ,a.tvcpName ,b.tvamLodgingName ,a.tvcpPrice ,a.tvcpStartDate ,a.tvcpEndDate ,a.tvcpDesc ,a.regDateTime from tvCoupon a
left join tvLodging b on a.tvamseq=b.tvamseq
where 1=1 and tvcpDelNy=0 and tvcpSeq=#{tvcpSeq}

mapper.update:
update tvCoupon a
	set 
		tvcpName = #{tvcpName}

		,tvcpPrice = #{tvcpPrice}
		,a.tvcpStartDate = #{tvcpStartDate}
		,a.tvcpEndDate = #{tvcpEndDate}
		,a.tvcpDesc = #{tvcpDesc}

	where 1=1
		and tvcpDelNy=0
		and tvcpSeq=#{tvcpSeq}

mapper.select:
select a.tvamSeq ,a.tvamLodgingName ,(select tvcdName from tvCode b where a.tvamCategoryCd=b.tvcdSeq) as tvamCategory ,a.tvamTelNumber ,a.tvamAddressFull ,a.tvamMaxPersonCount ,a.tvamTelNumber ,a.regDateTime from tvLodging a
left join tvMember b on a.tvmmseq=b.tvmmseq
where 1=1 and tvamDelNy=0 and a.tvmmSeq = #{tvmmSeq}
and a.tvamLodgingName like concat('%', #{shValue}, '%')
and a.tvamAddressFull like concat('%', #{shValue}, '%')
limit #{startRnumForMysql}, #{rowNumToShow}

