mapper.select:
select
tvmmName ,tvmmEmailAccount ,tvmmPassword ,tvmmAddress1 ,tvmmAddress2 ,tvmmAddressFull ,tvmmTelNumber ,tvmmDob ,regDateTime ,tvmmDesc
from tvMember a where 1=1 and tvmmHostNy=1 and tvmmSeq=#{tvmmSeq}

mapper.update:
update tvMember
set
tvmmName = #{tvmmName}
,tvmmEmailAccount = #{tvmmEmailAccount}
,tvmmPassword = #{tvmmPassword}
,tvmmTelNumber = #{tvmmTelNumber}
,tvmmAddress1 = #{tvmmAddress1} 
,tvmmAddress2 = #{tvmmAddress2}
,tvmmDob = #{tvmmDob}
,tvmmDesc = #{tvmmDesc} 

where
	tvmmSeq=#{tvmmSeq}

mapper.select:
select tvpmSeq ,tvamLodgingName ,tvpmStartDate ,tvpmEndDate ,tvpmtotalPrice ,tvmmEmailAccount ,tvmmName ,tvmmTelNumber ,tvpmAdNumber
from tvPayment a left join tvMember b on a.tvmmSeq=b.tvmmSeq left join tvLodging c on a.tvamSeq=c.tvamSeq where tvpmDelNy=0 and c.tvmmSeq = #{tvmmSeq}
and c.tvamLodgingName like concat('%', #{shValue}, '%') and b.tvmmName like concat('%', #{shValue}, '%') and b.tvmmEmailAccount like concat('%', #{shValue}, '%')
limit #{startRnumForMysql}, #{rowNumToShow}

mapper.update:
update
	tvPayment a
	left join tvMember d on a.tvmmSeq = d.tvmmSeq
set 
	d.tvmmTelNumber = #{tvmmTelNumber}
	,a.tvpmDesc = #{tvpmDesc}
	,a.tvpmAdNumber = #{tvpmAdNumber}
	,a.tvpmStartDate = #{tvpmStartDate}
	,a.tvpmEndDate = #{tvpmEndDate}





	where tvpmDelNy=0 and tvpmSeq = #{tvpmSeq}

update tvMemberUploaded set
	type = #{type}
	, defaultNy = #{defaultNy}
	, sort = #{sort}
	, originalName = #{originalName}
	, uuidName = #{uuidName}
	, ext = #{ext}
	, size = #{size}
	, delNy = 0
	, year = #{year}
	, month = #{month}
	, day = #{day}
	, tvmmSeq = #{tvmmSeq}
where 1=1
	and tvmmSeq = #{tvmmSeq}

mapper.insert:
insert into tvLodgingUploaded(
		type
		, defaultNy
		, sort
		, originalName
		, uuidName
		, ext
		, size
		, delNy
		, year
		, month
		, day
		, tvamSeq
	)values(
		 #{type}
		,#{defaultNy}
		,#{sort}
		,#{originalName}
		,#{uuidName}
		,#{ext}
		,#{size}
		,0
		,#{year}
		,#{month}
		,#{day}
		,#{tvamSeq}
)

mapper.select:
select count(*) from tvLodging a
left join tvLodgingUploaded b on a.tvamSeq= b.tvamSeq
where a.tvamSeq= #{tvamSeq}

mapper.update:

update tvLodgingUploaded set
	type = #{type}
	, defaultNy = #{defaultNy}
	, sort = #{sort}
	, originalName = #{originalName}
	, uuidName = #{uuidName}
	, ext = #{ext}
	, size = #{size}
	, delNy = 0
	, year = #{year}
	, month = #{month}
	, day = #{day}
	, tvamSeq = #{tvamSeq}
where 1=1
	and tvamSeq = #{tvamSeq}
	and sort = #{sort}
