mapper.select:
SELECT tvamSeq ,tvamLodgingName ,tvamDesc ,tvamAddressFull ,tvamLat ,tvamLng FROM tvLodging WHERE 1=1
AND tvamLng BETWEEN #{swLng} AND #{neLng}
AND tvamLat BETWEEN #{swLat} AND #{neLat}

mapper.insert:
		INSERT INTO tvPayment (
			tvpmSeq
 			, tvpmStartDate
			, tvpmEndDate
			, tvpmtotalPrice
			, tvamSeq
			, tvpmAdNumber
			, tvmmSeq
			, tvpmDelNy
			, regDateTime
		) VALUES (
			#{tvpmSeq}
 			, #{tvpmStartDate}
			, #{tvpmEndDate}
			, #{tvpmtotalPrice}
			, #{tvamSeq}
			, #{tvpmAdNumber}
			, #{tvmmSeq}
			, 0
			, now()
		)

mapper.select:
SELECT a.tvamSeq ,a.tvamMaxPersonCount ,a.tvamRoomCount ,a.tvamBedCount ,a.tvamShowerRoomCount ,a.tvamState ,a.tvamCity ,a.tvamTelNumber ,a.tvamAddressFull ,a.tvamCheckInTime ,a.tvamCheckOutTime ,a.tvamLodgingName ,a.tvamDesc ,a.tvamHostName ,a.tvmmSeq ,a.tvamAdultPrice
FROM travel_site.tvLodging a
WHERE 1=1 AND a.tvamDelNy = 0 AND a.tvamSeq = #{tvamSeq}

mapper.insert:
INSERT INTO tvPhotoMapLocation (
	tvplSeq
	,tvplLat
	,tvplLng
	,tvplTitle
	,tvplDesc
	,tvplEmotionCd
	,tvplDelNy
	,regDateTime
	,tvmmSeq
	) VALUES (
	#{tvplSeq}
	,#{tvplLat}
	,#{tvplLng}
	,#{tvplTitle}
	,#{tvplDesc}
	,#{tvplEmotionCd}
	,0
	,#{regDateTime}
	,#{tvmmSeq}
	)


	SELECT last_insert_id()

mapper.update:
UPDATE
		tvPhotoMapLocation
	SET 
		tvplLat = #{tvplLat}
		,tvplLng = #{tvplLng}
		,tvplTitle = #{tvplTitle}
		,tvplDesc = #{tvplDesc}
		,tvplEmotionCd = #{tvplEmotionCd}
	WHERE 1=1
		AND tvplSeq = #{tvplSeq}

UPDATE
	tvPhotoMapPhoto
SET
	type = #{type}
	,defaultNy = #{defaultNy}
	,sort = #{sort}
	,originalName = #{originalName}
	,uuidName = #{uuidName}
	,ext = #{ext}
	,size = #{size}
	,delNy = 0
	,pseq = #{pseq}
	,year = #{year}
	,month = #{month}
	,day = #{day}
WHERE 1=1
	AND pseq = #{pseq}
