mapper.select:
SELECT a.tvamSeq ,a.tvamMaxPersonCount ,a.tvamRoomCount ,a.tvamBedCount ,a.tvamShowerRoomCount ,a.tvamState ,a.tvamCity ,a.tvamTelNumber ,a.tvamAddressFull ,a.tvamCheckInTime ,a.tvamCheckOutTime ,a.tvamLodgingName ,a.tvamDesc ,a.tvamHostName ,a.tvmmSeq ,b.tvmmEmailAccount
FROM travel_site.tvLodging a
LEFT OUTER JOIN travel_site.tvMember AS b ON b.tvmmSeq = a.tvmmSeq WHERE 1=1 AND a.tvamDelNy = 0 AND a.tvamSeq = #{tvamSeq}
